package strings;

public class AmazingSubarrays {

	static public int solve(String A) {
		int res = 0;
		int n = A.length();
		for(int i=0;i<n;i++){
			char ch = A.charAt(i);
			if(ch=='a' || ch=='e' || ch=='i' || ch=='o' || ch=='u' || ch=='A' || ch=='E' || ch=='I' || ch=='O' || ch=='U')
				res = (res + n-i)% 10003; 
		}
		
		return res;
    }
	public static void main(String[] args) {
		System.out.println(solve(""));
	}
}
