package strings;

public class CountAndSay {
	
	public String countAndSay(int A) {
		String res = "1";
		
		for(int i=1;i<=A;i++){
			String res1 = "";
			String temp = new String(res);
			while(temp.length()>0){
				int counter = getCounter(temp,0);
				String ch = ""+temp.charAt(0);
				int num = Integer.parseInt(ch);
				//res.substring(j, counter);
				String r = String.valueOf(counter)+String.valueOf(num);
				temp=temp.substring(counter, temp.length());
				res1 = res1 + r;
				//temp = r + temp;
			}
			res = res1;
		}
		return res;
    }

	private int getCounter(String res, int j) {
		int cnt = 1;
		char ch = res.charAt(j);
		for(int i=j+1;i<res.length();i++){
			if(ch==res.charAt(i))
				cnt++;
			else
				break;
		}
		return cnt;
	}
	public static void main(String[] args) {
		CountAndSay cas = new CountAndSay();
		
		System.out.println(""
				+ ""=="l");
	}

}
